#include <linux/bpf.h>
#include <linux/sched.h>
#include <bpf/bpf_helpers.h>
#include <bpf/bpf_tracing.h>

char LICENSE[] SEC("license") = "GPL";

struct {
    __uint(type, BPF_MAP_TYPE_HASH);
    __uint(max_entries, 10240);
    __type(key, u32);
    __type(value, u64);
} task_last_run SEC(".maps");

struct {
    __uint(type, BPF_MAP_TYPE_PERCPU_ARRAY);
    __uint(max_entries, 1);
    __type(key, u32);
    __type(value, u64);
} cpu_idle_start SEC(".maps");

SEC("tp_btf/sched_switch")
int BPF_PROG(sched_switch, bool preempt, struct task_struct *prev, struct task_struct *next)
{
    u32 prev_pid = prev->pid;
    u32 next_pid = next->pid;
    u64 ts = bpf_ktime_get_ns();

    // Update last run time for the next task
    bpf_map_update_elem(&task_last_run, &next_pid, &ts, BPF_ANY);

    // Track CPU idle time
    u32 zero = 0;
    if (next_pid == 0) {
        // Switching to idle task
        bpf_map_update_elem(&cpu_idle_start, &zero, &ts, BPF_ANY);
    } else if (prev_pid == 0) {
        // Switching from idle task
        u64 *idle_start = bpf_map_lookup_elem(&cpu_idle_start, &zero);
        if (idle_start) {
            u64 idle_duration = ts - *idle_start;
            bpf_printk("CPU was idle for %llu ns\n", idle_duration);
        }
    }

    return 0;
}

SEC("tp_btf/sched_wakeup")
int BPF_PROG(sched_wakeup, struct task_struct *p)
{
    u32 pid = p->pid;
    u64 ts = bpf_ktime_get_ns();

    // Log task wakeup
    bpf_printk("Task %d woken up at %llu\n", pid, ts);

    return 0;
}

// Custom scheduler logic - prioritize tasks based on their last run time
SEC("struct_ops/sched_ext_ops")
int select_cpu(struct task_struct *p, int prev_cpu, u64 wake_flags)
{
    u32 pid = p->pid;
    u64 *last_run = bpf_map_lookup_elem(&task_last_run, &pid);
    u64 now = bpf_ktime_get_ns();

    if (last_run) {
        u64 time_since_last_run = now - *last_run;
        // Prefer CPUs for tasks that haven't run recently
        if (time_since_last_run > 1000000000) { // 1 second
            bpf_printk("Task %d hasn't run for %llu ns, prioritizing\n", pid, time_since_last_run);
        }
    }

    // For simplicity, return the previous CPU
    return prev_cpu;
}

