// Simple scheduler tracking using raw tracepoints
SEC("raw_tracepoint/sched_switch")
int trace_sched_switch(struct bpf_raw_tracepoint_args *ctx)
{
    // Get task PIDs from raw tracepoint args
    // args[0] = preempt, args[1] = prev task, args[2] = next task
    struct task_struct *prev = (void *)ctx->args[1];
    struct task_struct *next = (void *)ctx->args[2];

    u32 prev_pid, next_pid;
    bpf_probe_read_kernel(&prev_pid, sizeof(prev_pid), &prev->pid);
    bpf_probe_read_kernel(&next_pid, sizeof(next_pid), &next->pid);

    u64 ts = bpf_ktime_get_ns();

    // Update last run time for the next task
    bpf_map_update_elem(&task_last_run, &next_pid, &ts, BPF_ANY);

    // Track CPU idle time
    u32 zero = 0;
    if (next_pid == 0) {
        // Switching to idle task
        bpf_map_update_elem(&cpu_idle_start, &zero, &ts, BPF_ANY);
    } else if (prev_pid == 0) {
        // Switching from idle task
        u64 *idle_start = bpf_map_lookup_elem(&cpu_idle_start, &zero);
        if (idle_start) {
            u64 idle_duration = ts - *idle_start;
            bpf_printk("CPU was idle for %llu ns\n", idle_duration);
        }
    }

    return 0;
}

SEC("raw_tracepoint/sched_wakeup")
int trace_sched_wakeup(struct bpf_raw_tracepoint_args *ctx)
{
    struct task_struct *p = (void *)ctx->args[0];
    u32 pid;
    bpf_probe_read_kernel(&pid, sizeof(pid), &p->pid);

    u64 ts = bpf_ktime_get_ns();

    // Log task wakeup
    bpf_printk("Task %d woken up at %llu\n", pid, ts);

    return 0;
}

// Simple CPU frequency tracking
struct {
    __uint(type, BPF_MAP_TYPE_ARRAY);
    __uint(max_entries, 16); // Support up to 16 CPUs
    __type(key, u32);
    __type(value, u64);
} cpu_task_count SEC(".maps");

SEC("kprobe/finish_task_switch")
int BPF_KPROBE(finish_task_switch)
{
    u32 cpu = bpf_get_smp_processor_id();
    u64 *count = bpf_map_lookup_elem(&cpu_task_count, &cpu);
    if (count) {
        __sync_fetch_and_add(count, 1);
    } else {
        u64 init_count = 1;
        bpf_map_update_elem(&cpu_task_count, &cpu, &init_count, BPF_ANY);
    }

    bpf_printk("CPU %d finished task switch, total switches: %llu\n", cpu, count ? *count : 1);

    return 0;
}

